import io.kotest.matchers.ints.shouldBeExactly

internal class Test_2147_NumberOfWaysToDivideALongCorridorTest : StringSpec({

    val example1 = row("SSPPSPS", 3)
    val example2 = row("PPSPSP", 1)
    val example3 = row("S", 0)
    val example4 = row("SSPPSPS", 3)
    val example5 = row("SPPSSSSPPS", 1)
    val example6 = row("SPPPPPPPSPPPSPSSSPPPPPPPPPPPPPPPPPSPPPPPPPPPPPPPPPPSPPPPPSPSPPPPPPSPSPPSPSPPPSPSPPSSPPPPPSPPSSPP", 0)
    val example7 = row("PPPPPSPPSPPSPPPSPPPPSPPPPSPPPPSPPSPPPSPSPPPSPSPPPSPSPPPSPSPPPPSPPPPSPPPSPPSPPPPSPSPPPPSPSPPPPSPSPPPSPPSPPPPSPSPSS", 919999993)
    val example8 = row("", 596333475)

    "should correctly divide a long corridor" {
        forAll(example1, example2, example3, example4, example5, example6, example7, example8) { input, expectedOutput ->
            NumberOfWaysToDivideALongCorridor.numberOfWays(input) shouldBeExactly expectedOutput
        }
    }
})